import React, { useEffect, useState } from 'react'
import './RowPost.css'
import axios from '../axios'
import { API_KEY,imageUrl } from '../../constants/constants'

function RowPost() {
  const [movies, setMovies] = useState([])
  useEffect(() => {
    axios.get(`discover/tv?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&page=1&timezone=America%2FNew_York&include_null_first_air_dates=false&with_watch_monetization_types=flatrate&with_status=0&with_type=0`).then((response) => {
      console.log(response.data);
      setMovies(response.data.results)
    })
  }, [])
  return (
    <div className='row'>
      <h2>Netflix Originals</h2>
      <div className='posters'>
        {movies.map((obj)=>


          <img className='poster' src= {imageUrl+obj.backdrop_path} alt=""/>
        )}

      </div>

    </div>
  )
}

export default RowPost
